import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API = 'http://localhost:3001/api/notes';

const Diary = ({ token, setToken }) => {
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const fetchNotes = async () => {
    try {
      const res = await axios.get(API, { headers: { Authorization: `Bearer ${token}` } });
      setNotes(res.data);
    } catch (err) {
      alert(err.response.data.message || 'Error fetching notes');
    }
  };

  useEffect(() => { fetchNotes(); }, []);

  const addNote = async () => {
    try {
      await axios.post(API, { title, content }, { headers: { Authorization: `Bearer ${token}` } });
      setTitle(''); setContent('');
      fetchNotes();
    } catch (err) {
      alert(err.response.data.message || 'Error adding note');
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`${API}/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      fetchNotes();
    } catch (err) {
      alert(err.response.data.message || 'Error deleting note');
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setToken('');
  };

  return (
    <div>
      <h2>Your Diary</h2>
      <button onClick={logout}>Logout</button>
      <div>
        <input placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} /><br/>
        <textarea placeholder="Content" value={content} onChange={e => setContent(e.target.value)} /><br/>
        <button onClick={addNote}>Add Note</button>
      </div>
      <hr/>
      <ul>
        {notes.map(n => (
          <li key={n._id}>
            <h3>{n.title}</h3>
            <p>{n.content}</p>
            <button onClick={() => deleteNote(n._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Diary;
